var Counter=function(){"use strict";var n=function(){$(".js__counter").counterUp({formatter:_formatter, time: 2000})};return{init:function(){n()}}}();
$(document).ready(function(){
  setInterval(function () {
    Counter.init();
  },5000);
});

function _formatter(n) {
  var b=parseInt(n).toString();
  var len=b.length;
  if(len<=3){return b;}
  var r=len%3;
  return r>0?b.slice(0,r)+","+b.slice(r,len).match(/\d{3}/g).join(","):b.slice(r,len).match(/\d{3}/g).join(",");
}